var Editor = require('ui/editor')
  , keyById = require('transforms').keyById
  , React = require('react')
  , Thread = require('ui/thread').Thread
  , threadify = require('transforms').threadify
  ;

exports.render = function (article, comments, users) {
  var commentsEl = document.getElementById('js-comments-container')
    , editorComponent = Editor()
    , editorEl = document.getElementById('js-editor-container')
    , threadEl = document.getElementById('js-thread-container')
    , threadComponent = Thread({
      comments: threadify(comments)
      , depth: 0
      , users: keyById(users)
    })
    ;

  React.renderComponent(editorComponent, editorEl);
  React.renderComponent(threadComponent, threadEl);
};
