var coll = require('./collections').articles
  , findById = require('./helpers').findById
  , getAll = require('./helpers').getAll
  , schema = require('schemas/article')
  , validateAndSave = require('./helpers').validateAndSave
  ;

exports.get = function (id) {
  return findById(id, coll);
};

exports.getAll = function () {
  return getAll(coll);
};

exports.post = function (article) {
  return validateAndSave(article, schema, coll);
};
