var articlesSvc = require('services/articles')
  , fs = require('fs')
  , Mustache = require('mustache')
  ;

var articlesTemplate = fs.readFileSync(__dirname + '/templates/articles.mustache').toString();

module.exports = function () {
  return articlesSvc.getAll().then(function (articles) {
    return Mustache.render(articlesTemplate, {
      articles: articles
    });
  });
};
